import styled from 'styled-components';

export const Header = styled.div`
  width: 80em;
  padding: 60px;
`;

export const UserProfile = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Button = styled.button`
  margin-left: 15px;
  width: 300px;
  height: 35px;
  border-radius: 4px;
  font-family: 30;
  border: 0;
  border: 1px solid #ccc;
  background-color: #8159ca;
  color: #fff;
  font-weight: 600;
  padding: 0 5px;

  svg {
    margin-right: 5px;
  }
`;
